!function(t) {
    toastr.options = {
        closeButton: !0,
        debug: !1,
        newestOnTop: !1,
        progressBar: !0,
        positionClass: "toast-bottom-right",
        preventDuplicates: !1,
        onclick: null,
        showDuration: "300",
        hideDuration: "1000",
        timeOut: "5000",
        extendedTimeOut: "1000",
        showEasing: "swing",
        hideEasing: "linear",
        showMethod: "fadeIn",
        hideMethod: "fadeOut"
    },
    t.fn.ajaxSubmit = function(e) {
        var a = t(this)
          , o = t.extend({
            url: a.attr("action"),
            method: a.attr("method"),
            successCallback: function(t) {
            	console.log(t);
                a.find("input, button, select, textarea").prop("disabled", !1);
                if(t.success === true){                	
                	if (typeof t.message == 'string') { 
		                toastr.success(t.message);
		            } else { 
		                $.each(t.message, function(key, value){
		                	toastr.success(value);
		                })
		            } 
                }else{
                	if (typeof t.message == 'string') { 
		                toastr.error(t.message);
		            } else { 
		                $.each(t.message, function(key, value){
		                	toastr.error(value);
		                })
		            } 
                }
            },
            formdata: a.serialize(),
            datatype: "JSON",
            processdata: !1,
            contenttype: !1
        }, e);
        a.submit(function(e) {
            e.preventDefault(),
            t.ajax({
                url: o.url,
                method: o.method,
                dataType: o.datatype,
                data: "filetype" == o.formdata ? new FormData(a[0]) : o.formdata,
                processData: o.processdata,
                contentType: o.contenttype,
                beforeSend: function() {
                    a.find("input, button, select, textarea").prop("disabled", !0)
                },
                success: o.successCallback,
                error: function(t) {
                    console.log(t)
                }
            })
        })
    }
}(jQuery);
